import { useState } from "react";
import styled, { css } from "styled-components";
import GlobalStyles from "./styles/GlobalStyles";
import Typography from "./styles/Typography";

function App() {
  const [boxShadow, setBoxShadow] = useState({
    boxColor: "#f2f2f2",
    inset: true,
    x: 0,
    y: 0,
    blurRadius: 0,
    spreadRadius: 0,
    opacity: 0.75,
  });

  let Box = styled.div`
    ${(props) =>
      props.boxStyle &&
      css`
        background-color: ${boxShadow.boxColor};
        position: absolute;
        width: 60%;
        height: 60%;
        margin: auto;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        text-align: center;
      `}
    box-shadow: ${boxShadow.inset ? `inset` : ``}
                ${`${boxShadow.x}px`} ${`${boxShadow.y}px`}
                ${`${boxShadow.blurRadius}px`}
                ${`${boxShadow.spreadRadius}px`}
                rgba(0,0,0,0,${boxShadow.opacity});
  `;

  return (
    <>
      <GlobalStyles />
      <Typography />
      <StyledApp>
        <Header>
          <h5>BoxShadow</h5>
        </Header>
        <Container>
          <Left>
            <WhiteSpace>
              <Box boxStyle></Box>
            </WhiteSpace>
          </Left>
          <Right>
            <Controls>
              <Color>
                <label for="color">Pick a color: </label>
                <input
                  id="color"
                  type="color"
                  value={boxShadow.boxColor}
                  onChange={(event) =>
                    setBoxShadow({ ...boxShadow, boxColor: event.target.value })
                  }
                />
              </Color>

              <label for="inset">Inset </label>
              <input
                id="inset"
                type="checkbox"
                checked={boxShadow.inset ? true : false}
                onChange={(event) =>
                  setBoxShadow({ ...boxShadow, inset: !boxShadow.inset })
                }
              />
            </Controls>
          </Right>
        </Container>
      </StyledApp>
    </>
  );
}

export default App;

const StyledApp = styled.div``;
const Container = styled.section`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
`;
const Left = styled.aside`
  width: 50%;
  height: 100vh;
  display: flex;
  justify-content: right;
`;

const WhiteSpace = styled.div`
  background-color: #fff;
  width: 50%;
  height: 50%;
  margin-right: 10em;
  position: relative;
`;

const Right = styled.aside`
  width: 50%;
  height: 100vh;
  display: flex;
  justify-content: left;
`;

const Controls = styled.div`
  background-color: #fff;
  width: 50%;
  height: 50%;
  margin-left: 0em;
`;

const Header = styled.div`
  margin: 3em 3em;
  text-align: center;
`;
