import { useState } from "react";
import styled, { css } from "styled-components";
import GlobalStyles from "./styles/GlobalStyles";
import Typography from "./styles/Typography";

function App() {
  const [boxShadow, setBoxShadow] = useState({
    boxcolor: "#303030",
    inset: true,
    x: 0,
    y: 0,
    blurRadius: 0,
    spreadRadius: 0,
    opacity: 0.75,
  });

  let Box = styled.div`
    ${(props) =>
      props.otherStyle &&
      css`
        background-color: ${boxShadow.boxcolor};
        height: 440px;
        display: flex;
      `}
    box-shadow: 0px 0px 0px 20px rgba(0,0,0,0,4)
  `;

  return (
    <>
      <GlobalStyles />
      <Typography />
      <StyledApp>
        <Header>
          <h5>BoxShadow</h5>
        </Header>
        <Container>
          <Left>
            <WhiteSpace>
              <BoxShadow></BoxShadow>
            </WhiteSpace>
          </Left>
          <Right>
            <Controls>
              <input type="text" />
            </Controls>
          </Right>
        </Container>
      </StyledApp>
    </>
  );
}

export default App;

const StyledApp = styled.div``;
const Container = styled.section`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: center;
`;
const Left = styled.aside`
  width: 50%;
  height: 100vh;
  display: flex;
  justify-content: right;
`;

const WhiteSpace = styled.div`
  background-color: #fff;
  width: 50%;
  height: 50%;
  margin-right: 10em;
  position: relative;
`;

const BoxShadow = styled.div`
  position: absolute;
  width: 60%;
  height: 60%;
  background-color: pink;
  margin: auto;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  text-align: center;
`;

const Right = styled.aside`
  width: 50%;
  height: 100vh;
  display: flex;
  justify-content: left;
`;

const Controls = styled.div`
  background-color: #fff;
  width: 50%;
  height: 50%;
  margin-left: 0em;
`;

const Header = styled.div`
  margin: 3em 3em;
  text-align: center;
`;
